mapper.insert:

SELECT IFNULL(MAX(REQ_ID),0)+1 AS NTT_NO  FROM LETTNBBSLOG


INSERT INTO LETTNBBSLOG
 (
	REQ_ID, NTT_ID, BBS_ID, NTCR_ID, NTCR_NM, FRST_REGIST_PNTTM, FRST_REGISTER_ID
 )
VALUES
 ( 
	#{nttNo},#{nttId}, #{bbsId}, #{ntcrId}, #{ntcrNm}, SYSDATE(), #{frstRegisterId}
 )

mapper.select:
select IFNULL(dept,'전체') as deptNmS , IFNULL(chk,'소계') as scoreS, count(1) as scoreA from ( select user_nm, cdc.code_dc as dept , cdb.code_dc as title, IFNULL(mmb.cnt,0) AS cnt, IFNULL(mmb.chk,'X') AS chk from COMVNUSERMASTER mb LEFT OUTER JOIN lettccmmndetailcode cdb ON mb.TITLE_CODE = cdb.CODE AND cdb.CODE_ID = 'IBK002' AND cdb.USE_AT ='Y' LEFT OUTER JOIN lettccmmndetailcode cdc ON mb.DEPT_CODE = cdc.CODE AND cdc.CODE_ID = 'IBK101' AND cdc.USE_AT ='Y' LEFT OUTER JOIN (
select conect_id, case when count(1) > 0 and count(1) < 3 then 'A' when count(1) > 2 then 'B' ELSE 'X' END AS chk, count(1) as cnt from lettnloginlog where creat_dt > '2023-05-08' group by conect_id
) mmb ON mmb.conect_id = mb.user_id where mb.USER_SE = 'GNR' and mb.DEPT_CODE is not null and mb.POSITION_CODE not like 'A%' ) asd group by dept, chk with rollup


SELECT IFNULL(MAX(RDCNT),0)+1 AS RDCNT FROM LETTNBBS
WHERE BBS_ID = #{bbsId}
AND NTT_ID = #{nttId}

mapper.update:

UPDATE LETTNBBS SET
	RDCNT = #{inqireCo},
	LAST_UPDUSR_ID = #{lastUpdusrId},
	LAST_UPDT_PNTTM = SYSDATE()
WHERE BBS_ID = #{bbsId}
AND NTT_ID = #{nttId}
