mapper.select:
SELECT a.NTT_ID, a.NTT_SJ, a.NTT_CN, a.FRST_REGISTER_ID, IFNULL(b.USER_NM, a.NTCR_NM) as FRST_REGISTER_NM, DATE_FORMAT(a.FRST_REGIST_PNTTM, '%Y-%m-%d') as FRST_REGIST_PNTTM, a.RDCNT, a.PARNTSCTT_NO, a.ANSWER_AT, a.ANSWER_LC, a.USE_AT, a.ATCH_FILE_ID, a.BBS_ID, a.NTCE_BGNDE, a.NTCE_ENDDE, a.TEAM_NM, a.IMG_URL, a.SELECT_IMG FROM LETTNBBS a LEFT OUTER JOIN COMVNUSERMASTER b ON a.FRST_REGISTER_ID = b.ESNTL_ID WHERE a.BBS_ID = #{bbsId}
AND a.FRST_REGISTER_ID = #{frstRegisterId}
AND a.USE_AT = #{useAt}
AND a.NTT_SJ LIKE CONCAT ('%', #{searchWrd},'%')
AND a.NTT_CN LIKE CONCAT ('%', #{searchWrd},'%')
AND b.USER_NM LIKE CONCAT ('%', #{searchWrd},'%')
ORDER BY a.SORT_ORDR DESC, NTT_NO ASC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT a.NTT_ID, a.NTT_SJ, a.NTT_CN, a.FRST_REGISTER_ID, IFNULL(b.USER_NM, a.NTCR_NM) as FRST_REGISTER_NM, DATE_FORMAT(a.FRST_REGIST_PNTTM, '%Y-%m-%d') as FRST_REGIST_PNTTM, a.RDCNT, a.PARNTSCTT_NO, a.ANSWER_AT, a.ANSWER_LC, a.USE_AT, a.ATCH_FILE_ID, a.BBS_ID, a.NTCE_BGNDE, a.NTCE_ENDDE, a.TEAM_NM, a.IMG_URL, a.SELECT_IMG , (SELECT IFNULL((SELECT c.score FROM lettnbbsscore c WHERE c.NTT_ID = a.NTT_ID AND c.BBS_ID = a.BBS_ID AND c.WRTER_ID = #{exmnId} AND c.USE_AT = 'Y' LIMIT 1), 0)) AS SCORE , (SELECT IFNULL((SELECT ROUND(AVG(c.score), 1) FROM lettnbbsscore c WHERE c.NTT_ID = a.NTT_ID AND c.BBS_ID = a.BBS_ID AND c.USE_AT = 'Y' LIMIT 1), 0)) AS SCORE_A , (SELECT count(1) FROM lettnbbsscore c WHERE c.NTT_ID = a.NTT_ID AND c.BBS_ID = a.BBS_ID AND c.USE_AT = 'Y' LIMIT 1) AS SCORE_CNT , (SELECT count(1) FROM lettncomment c WHERE c.NTT_ID = a.NTT_ID AND c.BBS_ID = a.BBS_ID AND c.USE_AT = 'Y' LIMIT 1) AS COMMENT_CNT FROM LETTNBBS a LEFT OUTER JOIN COMVNUSERMASTER b ON a.FRST_REGISTER_ID = b.ESNTL_ID WHERE a.BBS_ID = #{bbsId}
AND a.USE_AT = #{useAt}
AND a.NTT_SJ LIKE CONCAT ('%', #{searchWrd},'%')
AND a.NTT_CN LIKE CONCAT ('%', #{searchWrd},'%')
AND b.USER_NM LIKE CONCAT ('%', #{searchWrd},'%')
ORDER BY a.SORT_ORDR DESC, NTT_NO ASC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(a.NTT_ID) FROM LETTNBBS a LEFT OUTER JOIN COMVNUSERMASTER b ON a.FRST_REGISTER_ID = b.ESNTL_ID WHERE a.BBS_ID = #{bbsId}
AND a.FRST_REGISTER_ID = #{frstRegisterId}
AND a.USE_AT = #{useAt}
AND a.NTT_SJ LIKE CONCAT ('%', #{searchWrd},'%')
AND a.NTT_CN LIKE CONCAT ('%', #{searchWrd},'%')
AND b.USER_NM LIKE CONCAT ('%', #{searchWrd},'%')


SELECT IFNULL(MAX(RDCNT),0)+1 AS RDCNT FROM LETTNBBS
WHERE BBS_ID = #{bbsId}
AND NTT_ID = #{nttId}


SELECT
	IFNULL(MAX(NTT_NO),0)+1 AS NTT_NO
FROM
	LETTNBBS	
WHERE
	BBS_ID = #{bbsId}
AND
	SORT_ORDR = #{sortOrdr}
AND 
	NTT_ID = #{nttId}

mapper.update:

UPDATE LETTNBBS SET 
	RDCNT = #{inqireCo},
	LAST_UPDUSR_ID = #{lastUpdusrId},
	LAST_UPDT_PNTTM = SYSDATE()
WHERE BBS_ID = #{bbsId}
AND NTT_ID = #{nttId}


UPDATE LETTNBBS SET 
	NTT_NO = #{nttNo}
WHERE NTT_ID = #{nttId}


UPDATE LETTNBBS
SET USE_AT = 'N'
WHERE BBS_ID = #{bbsId}
AND NTT_ID = #{nttId}

