mapper.update:

UPDATE LETTNBBS SET 
	NTT_NO = NTT_NO + 1
WHERE BBS_ID = #{bbsId} AND SORT_ORDR = #{sortOrdr}
AND NTT_NO > #{nttNo}


UPDATE LETTNBBS SET 
	NTT_NO = #{nttNo}
WHERE BBS_ID = #{bbsId} 
AND NTT_ID = #{nttId}

mapper.select:
SELECT IFNULL(MAX(ANSWER_NO),0)+1 AS ANSWER_NO
FROM LETTNCOMMENT
WHERE BBS_ID = #{bbsId}
  AND NTT_ID = #{nttId}

mapper.update:
UPDATE LETTNCOMMENT SET
	ANSWER = #{answer},
	USE_AT = #{useAt},
	LAST_UPDUSR_ID = #{lastUpdusrId},
	LAST_UPDT_PNTTM = SYSDATE()
WHERE BBS_ID = #{bbsId}
  AND NTT_ID = #{nttId}
  AND ANSWER_NO = #{answerNo}
  AND WRTER_ID = #{exmnId}
